import { createMuiTheme } from '@material-ui/core/styles';

// const arcBlue = "#5C0923";
const arcBlue = "#4e6876";
const arcOrange = "#e2d1a6";
// const arcOrange = "#FFFFFF";

export default createMuiTheme({
  palette: {
    common: {
      blue: `${arcBlue}`,
      orange: `${arcOrange}`
    },
    primary: {
      main: `${arcBlue}`,
    },
    secondary: {
      main: `${arcOrange}`
    }
  },
  typography: {
    tab: {
      fontFamily: "Raleway",
      textTransform: "none",
      fontWeight: 700,
      fontSize: "1rem",
    },
     estimate: {
      fontFamily: "Pacifico",
      fontSize: "1rem",
      textTransform: "none",
      color: "white",
     },
     header1: {
      fontFamily: "Goblin One",
      fontWeight: "700",
      fontSize: "3.5em"
     },
     header2: {
      fontWeight: "700",
      fontSize: "2.5em"
     },
     header3: {
      fontWeight: "700",
      fontSize: "2em"
     },
     header4: {
      fontSize: "1em"
     },
     h2: {
       fontFamily: "Raleway",
       fontWeight: "700",
       fontSize: "2.5em"
     },
     h3: {
       fontFamily: "Raleway",
       fontSize: "2.5em",
     },
     h4: {
       fontFamily: "Raleway",
       fontSize: "1.75em"
     }
  }
});
